import React from "react";
import { useDispatch } from "react-redux";
import "./collection-item.styles.scss";
import CustomButton from "../custom-button/custom-button.component";

export default function CollectionItem({ item }) {
  const dispatch = useDispatch();
  const items = {image,name,price}

  const addItemToStore = (image,name,price) => {
    dispatch({ type: "ADD_ITEM", payload: items });
  };

  return (
    <>
      {item.map((items) => (
        <div className="collection-item">
          <div
            className="image"
            style={{ backgroundImage: `url(${items.imageUrl})` }}
          />
          <div className="collection-footer">
            <span className="name">{items.name}</span>
            <span className="price">{items.price}$</span>
          </div>
          <CustomButton inverted onClick={() => addItemToStore(items.imageUrl,items.name,items.price)}>
            {" "}
            Add To Cart
          </CustomButton>
        </div>
      ))}
    </>
  );
}
