const INITIAL_STATE = {
  collections: [],
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "SET_DATA_FROM_FIRESTORE":
      return {
        ...state,
        collections:  Object.keys(action.payload),
      };
    default:
      return state;
  }
};

export default shopReducer;
